import React, { useState } from "react";

const AddMemeForm = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [categories, setCategories] = useState("");
  const [image, setImage] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("categories", categories);
    formData.append("image", image);

    try {
      const response = await fetch("http://localhost:5000/api/memes", {
        method: "POST",
        body: formData, // Removed "Content-Type": "application/json"
      });
      const data = await response.json();
      console.log("Meme added:", data);
    } catch (error) {
      console.error("Error uploading meme:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" required />
      <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" required />
      <input type="text" value={categories} onChange={(e) => setCategories(e.target.value)} placeholder="Categories" required />
      <input type="file" onChange={(e) => setImage(e.target.files[0])} required />
      <button type="submit">Upload Meme</button>
    </form>
  );
};

export default AddMemeForm;
