import React, { useState, useEffect } from "react";
import { auth, provider, signInWithPopup, signOut } from "./firebase"; // Import Firebase auth
import logo from "./wiki-logo.png";
import "./App.css";
import AddMemeForm from "./Components/AddMemeForm";
import "bootstrap/dist/css/bootstrap.min.css";

const App = () => {
  const [memes, setMemes] = useState([]);
  const [selectedMeme, setSelectedMeme] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/memes")
      .then((res) => res.json())
      .then((data) => setMemes(data));

    // Listen for auth state changes
    auth.onAuthStateChanged((user) => {
      setUser(user);
    });
  }, []);

  const handleSignIn = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
    } catch (error) {
      console.error("Error signing in:", error);
    }
  };

  const handleSignOut = async () => {
    try {
      await signOut(auth);
      setUser(null);
    } catch (error) {
      console.error("Error signing out:", error);
    }
  };

  const filteredMemes = memes.filter((meme) =>
    meme.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="App">
      <header className="App-header">
        <ul>
          <li>
            <img src={logo} className="App-logo" alt="logo" />
          </li>
          <li>Your encyclopedia of internet memes!</li>
          <li>
            <input
              type="text"
              placeholder="Search for a meme..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="input-group"
            />
          </li>
          <li>
            {user ? (
              <>
                <span>Welcome, {user.displayName}!</span>
                <button onClick={handleSignOut} className="btn btn-danger">
                  Logout
                </button>
              </>
            ) : (
              <button onClick={handleSignIn} className="btn btn-primary">
                Login with Google
              </button>
            )}
          </li>
        </ul>
      </header>
      <div className="row align-items-start">
        <div className="col">
          <h3>Add Meme to WikiMemes</h3>
          {user ? <AddMemeForm /> : <p>Please login to add memes.</p>}
        </div>
        <div className="col">
          <main>
            {selectedMeme ? (
              <div className="meme-details">
                <h2>{selectedMeme.title}</h2>
                <img
                  src={`http://localhost:5000${selectedMeme.image_url}`}
                  alt={selectedMeme.title}
                  className="meme-details-image"
                />
                <p>{selectedMeme.description}</p>
                <p>
                  <strong>Categories:</strong> {selectedMeme.categories}
                </p>
                <button
                  className="btn btn-danger"
                  onClick={() => setSelectedMeme(null)}
                >
                  Close
                </button>
              </div>
            ) : (
              <div className="meme-list">
                {filteredMemes.map((meme) => (
                  <div
                    key={meme.id}
                    className="meme-card"
                    onClick={() => setSelectedMeme(meme)}
                  >
                    <img
                      src={`http://localhost:5000${meme.image_url}`}
                      alt={meme.title}
                      className="meme-image"
                    />
                    <p style={{ color: "whitesmoke" }}>See meme details..</p>
                    <h3 className="meme-title">{meme.title}</h3>
                    <hr style={{ color: "orange" }}></hr>
                  </div>
                ))}
              </div>
            )}
          </main>
        </div>
        <div className="col"></div>
      </div>
    </div>
  );
};

export default App;
