import React, {useState} from 'react';
import logo from './wiki-logo.png';
import './App.css';
import memesData from "./data/memes.json";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [selectedMeme, setSelectedMeme] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  const filteredMemes = memesData.filter((meme) =>
    meme.title.toLowerCase().includes(searchQuery.toLowerCase())
  );
  return (
    <div className="App">
      <header className="App-header">
        <ul>          
          <li><img src={logo} className="App-logo" alt="logo" /></li>
          <li>Your encyclopedia of internet memes!</li>
          <li><input 
                type="text"
                placeholder="Search for a meme..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="input-group"
              />
          </li> 
        </ul>
      </header>
      <div className='row align-items-start'>
        <div className='col'>

        </div>
        <div className='col'>
              <main>
                <div className="meme-list">
                  {filteredMemes.map((meme) => (
                    <div
                      key={meme.id}
                      className="meme-card"
                      onClick={() => setSelectedMeme(meme)}
                    >
                      <img src={meme.image} alt={meme.title} className="meme-image" />
                      <h3 className="meme-title">{meme.title}</h3>
                      <hr style={{border: "20px", color: "white"}}></hr>
                    </div>
                  ))}
                </div>

                {selectedMeme && (
                  <div className="meme-details">
                    <h2>{selectedMeme.title}</h2>
                    <img
                      src={selectedMeme.image}
                      alt={selectedMeme.title}
                      className="meme-details-image"
                    />
                    <p>{selectedMeme.description}</p>
                    <p>
                      <strong>Categories:</strong> {selectedMeme.categories.join(", ")}
                    </p>
                    <button className='btn btn-danger' onClick={() => setSelectedMeme(null)}>Close</button>
                  </div>
                )}
              </main>
        </div>
          <div className='col'>

          </div>
      </div>
    </div>
  );
}

export default App;
