import React, { useState, useEffect } from "react";

const Shop = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await fetch("https://api.printify.com/v1/catalog/products.json", {
          method: "GET",
          headers: {
            "Authorization": `Bearer ${process.env.REACT_APP_PRINTIFY_API_TOKEN}`,
          },
        });
        const data = await res.json();
        setProducts(data);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div className="shop-container">
      <h2>Shop DonTheMeme Merch</h2>
      <div className="product-grid">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <img
              src={product.images[0]?.url || "default-placeholder.jpg"}
              alt={product.title}
              className="product-image"
            />
            <h3>{product.title}</h3>
            <p>{product.price}</p>
            <a
              href={`https://printify.com/products/${product.id}`}
              target="_blank"
              rel="noopener noreferrer"
              className="buy-button"
            >
              Buy Now
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Shop;
