import React, { useState, useEffect } from "react";

const ScrapedMemes = () => {
  const [memes, setMemes] = useState([]);
  const [selectedMeme, setSelectedMeme] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/scraped-memes")
      .then((res) => res.json())
      .then((data) => setMemes(data))
      .catch((err) => console.error("Error fetching memes:", err));
  }, []);

  return (
    <div>
      <h2>Scraped Memes</h2>
      <div className="meme-grid">
        {memes.map((meme) => (
          <img
            key={meme.id}
            src={meme.image_url}
            alt="Scraped Meme"
            className="meme-thumbnail"
            onClick={() => setSelectedMeme(meme.image_url)}
          />
        ))}
      </div>

      {selectedMeme && (
        <div className="overlay" onClick={() => setSelectedMeme(null)}>
          <div className="modal">
            <img src={selectedMeme} alt="Full Meme" className="full-meme" />
            <button className="close-btn" onClick={() => setSelectedMeme(null)}>X</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ScrapedMemes;
